mapper.select:
select id
from rooms
where start_time is not null and end_time is null and (data_type='랜덤' or  data_type='그룹');

mapper.update:
update rooms
set flag_available = 0
where DATEDIFF(NOW(), end_time) = 1 and (data_type = '랜덤' or data_type  = '그룹');

choose.when:
UPDATE participants
SET flag_win = 0
WHERE room_id = #{roomId}

mapper.update:
UPDATE rooms
SET end_time = now()
WHERE id = #{roomId}
