mapper.update:
update users set
nickname = #{nickname}
where id = #{id}

update users set
email_password = #{emailPassword}
where id = #{id}

update users set
identifier = #{identifier}, flag_identifier = #{flagIdentifier}
where
id = #{id}

update users set
image_url = #{imageUrl}, image_uuid = #{imageUuid}
where id = #{id}

mapper.select:
select id, from_user_id, to_user_id
from follows
where
from_user_id = #{fromUserId}
and to_user_id = #{toUserId}

mapper.delete:
delete from follows where
from_user_id = #{fromUserId}
and to_user_id =
#{toUserId}

mapper.insert:
update users
set bio = #{bio}
where id = #{userId}

mapper.select:
select u.bio, u.id, u.emailId, u.nickname
from users u
where id = #{userId}
